styles.globalCSS:
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 0 0% 100%;
    --foreground: 222.2 84% 4.9%;

    --card: 0 0% 100%;
    --card-foreground: 222.2 84% 4.9%;

    --popover: 0 0% 100%;
    --popover-foreground: 222.2 84% 4.9%;

    --primary: 222.2 47.4% 11.2%;
    --primary-foreground: 210 40% 98%;

    --secondary: 210 40% 96.1%;
    --secondary-foreground: 222.2 47.4% 11.2%;

    --muted: 210 40% 96.1%;
    --muted-foreground: 215.4 16.3% 46.9%;

    --accent: 210 40% 96.1%;
    --accent-foreground: 222.2 47.4% 11.2%;

    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 210 40% 98%;

    --border: 214.3 31.8% 91.4%;
    --input: 214.3 31.8% 91.4%;
    --ring: 222.2 84% 4.9%;

    --radius: 0.5rem;

    --sidebar-background: 0 0% 98%;
    --sidebar-foreground: 240 5.3% 26.1%;
    --sidebar-primary: 240 5.9% 10%;
    --sidebar-primary-foreground: 0 0% 98%;
    --sidebar-accent: 240 4.8% 95.9%;
    --sidebar-accent-foreground: 240 5.9% 10%;
    --sidebar-border: 220 13% 91%;
    --sidebar-ring: 217.2 91.2% 59.8%;
  }

  .dark {
    --background: 222.2 84% 4.9%;
    --foreground: 210 40% 98%;
    --card: 222.2 84% 4.9%;
    --card-foreground: 210 40% 98%;
    --popover: 222.2 84% 4.9%;
    --popover-foreground: 210 40% 98%;
    --primary: 210 40% 98%;
    --primary-foreground: 222.2 47.4% 11.2%;
    --secondary: 217.2 32.6% 17.5%;
    --secondary-foreground: 210 40% 98%;
    --muted: 217.2 32.6% 17.5%;
    --muted-foreground: 215 20.2% 65.1%;
    --accent: 217.2 32.6% 17.5%;
    --accent-foreground: 210 40% 98%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 210 40% 98%;
    --border: 217.2 32.6% 17.5%;
    --input: 217.2 32.6% 17.5%;
    --ring: 212.7 26.8% 83.9%;
    --sidebar-background: 240 5.9% 10%;
    --sidebar-foreground: 240 4.8% 95.9%;
    --sidebar-primary: 224.3 76.3% 48%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 240 3.7% 15.9%;
    --sidebar-accent-foreground: 240 4.8% 95.9%;
    --sidebar-border: 240 3.7% 15.9%;
    --sidebar-ring: 217.2 91.2% 59.8%;
  }
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
  }

  /* Sales Deck Theme Utilities */
  .glass-effect {
    @apply bg-white/70 backdrop-blur-sm border border-white/20 shadow-lg;
  }

  .gradient-text {
    @apply bg-gradient-to-r from-brand-primary to-brand-accent bg-clip-text text-transparent;
  }

  .hero-gradient {
    @apply bg-gradient-to-br from-brand-dark to-brand-primary;
  }

  .section-gradient {
    @apply bg-gradient-to-b from-gray-50 to-white;
  }

  .card-hover {
    @apply hover:shadow-xl transition-all hover:-translate-y-1 hover:border-brand-accent/30;
  }

  .floating-elements::before {
    content: '';
    @apply absolute -top-4 -right-4 w-8 h-8 border border-brand-accent/30 rounded-md rotate-12 z-0 hidden md:block;
    transition: transform 0.3s ease;
  }

  .floating-elements::after {
    content: '';
    @apply absolute -bottom-3 -left-3 w-6 h-6 border border-brand-primary/30 rounded-full z-0 hidden md:block;
    transition: transform 0.3s ease;
  }

  .floating-elements:hover::before {
    transform: rotate(45deg);
  }

  .floating-elements:hover::after {
    transform: scale(1.5);
  }
}

@layer utilities {
  .animate-fade-in {
    opacity: 0;
    animation: fadeIn 0.5s ease-out forwards;
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-float.delay-200 {
    animation-delay: 200ms;
  }

  @keyframes fadeIn {
    from {
      opacity: 0;
      transform: translateY(10px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes float {
    0%, 100% {
      transform: translateY(0px);
    }
    50% {
      transform: translateY(-10px);
    }
  }

  /* Background patterns */
  .bg-pattern {
    background-image: 
      radial-gradient(circle at 1px 1px, rgba(30, 58, 80, 0.08) 1px, transparent 0);
    background-size: 20px 20px;
  }

  .bg-hero-pattern {
    background-image: 
      radial-gradient(circle at 25% 25%, rgba(53, 161, 98, 0.1) 0%, transparent 50%),
      radial-gradient(circle at 75% 75%, rgba(30, 58, 80, 0.1) 0%, transparent 50%);
  }
}

/* Print styles */
@media print {
  .print\\:py-12 {
    padding-top: 3rem;
    padding-bottom: 3rem;
  }

  .print\\:py-10 {
    padding-top: 2.5rem;
    padding-bottom: 2.5rem;
  }

  .print\\:py-4 {
    padding-top: 1rem;
    padding-bottom: 1rem;
  }

  .print\\:text-3xl {
    font-size: 1.875rem;
    line-height: 2.25rem;
  }

  .print\\:text-lg {
    font-size: 1.125rem;
    line-height: 1.75rem;
  }

  .print\\:h-8 {
    height: 2rem;
  }

  .print\\:w-8 {
    width: 2rem;
  }

  .print\\:mb-8 {
    margin-bottom: 2rem;
  }

  .print\\:mt-10 {
    margin-top: 2.5rem;
  }

  .print\\:hidden {
    display: none;
  }

  .print\\:block {
    display: block;
  }

  .print\\:static {
    position: static;
  }

  .print\\:shadow-none {
    box-shadow: none;
  }

  .print\\:page-break-before {
    page-break-before: always;
  }

  .print\\:bg-brand-primary {
    background-color: #1E3A50;
  }

  .print\\:hover\\:translate-y-0:hover {
    transform: translateY(0);
  }

  .print\\:hover\\:shadow-none:hover {
    box-shadow: none;
  }

  .print\\:hover\\:scale-100:hover {
    transform: scale(1);
  }

  .print\\:translate-y-0 {
    transform: translateY(0);
  }

  .print\\:translate-x-0 {
    transform: translateX(0);
  }

  .print\\:opacity-100 {
    opacity: 1;
  }

  .print\\:animate-none {
    animation: none;
  }

  .print\\:scroll-mt-0 {
    scroll-margin-top: 0;
  }
}

